const themes=[{image:'london.jpg',colour:'#FF0000',location:'London, United Kingdom'},{image:'new-york.jpg',colour:'#FC8206',location:'New York City, USA'},{image:'venice.png',colour:'#00BFFF',location:'Venice, Italy'},{image:'malta.png',colour:'#4CBD08',location:'Sliema, Malta'},{image:'paris.png',colour:'#AF9348',location:'Paris, France'},{image:'naples.png',colour:'#E9CE14',location:'Naples, Italy'}];let nextTheme=0;const changeTheme=newThemeIndex=>{const newTheme=themes[newThemeIndex];document.getElementById('header-background').style.backgroundImage=`url("images/${newTheme.image}")`;document.getElementById('image-location').innerHTML=newTheme.location;Array.from(document.getElementsByClassName('section-title')).forEach(title=>{title.style.backgroundImage=`url("images/${newTheme.image}")`});if(document.getElementsByTagName('style').length){document.body.removeChild(document.getElementsByTagName('style')[0])}
const style=document.createElement('style');style.innerHTML=`.pc, a, .pc-hover:hover {color: ${newTheme.colour};} .bg-pc, .bg-pc-hover:hover {background-color: ${newTheme.colour};} .bc-pc-hover:hover, .theme-duration-bar::after {border-color: ${newTheme.colour};}`;document.body.appendChild(style)}
const manageTheme=()=>{if(document.getElementById('header-background').classList){document.getElementById('header-background').classList.remove('theme-duration-bar')}
void document.getElementById('header-background').offsetWidth;document.getElementById('header-background').classList.add('theme-duration-bar');nextTheme++;if(nextTheme>=themes.length){nextTheme=0}
let imageLoaded=!1;const nextImage=new Image();nextImage.onload=function(){imageLoaded=!0}
nextImage.src=`images/${themes[nextTheme].image}`;setTimeout(function(){if(imageLoaded){changeTheme(nextTheme);manageTheme()}else{nextImage.onload=function(){changeTheme(nextTheme);manageTheme()}}},20000)}
document.addEventListener('DOMContentLoaded',function(event){manageTheme()})